--
-- 表的结构 `tqb_subscribe`
--

CREATE TABLE IF NOT EXISTS `tqb_subscribe` (
  `sub_ID` int(11) NOT NULL AUTO_INCREMENT,
  `sub_Email` varchar(50) NOT NULL DEFAULT '',
  `sub_IP` varchar(15) NOT NULL DEFAULT '',
  `sub_Time` int(11) NOT NULL DEFAULT '0',
  `sub_ItemId` tinyint(3) NOT NULL DEFAULT '0',
  PRIMARY KEY (`sub_ID`),
  KEY `tqb_sub_Email` (`sub_Email`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

